    // EventTarget 就是一个调度中心

    class EventTarget {
        constructor() {
            this.dep = {}
        }

        on(key, fn) {
            this.dep[key] = fn;
        }

        emit(key) {
            typeof this.dep[key] === 'function' ? this.dep[key]() : ''
        }
    }

    let eventTarget = new EventTarget()

    eventTarget.on('click', function() {console.log(1)})
    eventTarget.emit('click')
